INSERT INTO public.articles (title, slug, summary, content, excerpt, published_date, section, author, is_published) VALUES
('Zaufanie, które więdnie', 'zaufanie-ktore-wiednie', 'Esej o kapitale społecznym i erozji zaufania w społeczeństwie', 'W dzisiejszych czasach obserwujemy systematyczną erozję zaufania społecznego. To zjawisko ma daleko idące konsekwencje dla funkcjonowania demokratycznego społeczeństwa. Zaufanie społeczne, będące fundamentem kapitału społecznego, stanowi kluczowy element stabilności politycznej i ekonomicznej. Jego spadek prowadzi do polaryzacji społecznej, wzrostu populizmu i osłabienia instytucji demokratycznych. Artykuł analizuje przyczyny tego zjawiska i wskazuje możliwe drogi wyjścia z kryzysu zaufania.', 'Esej o kapitale społecznym i erozji zaufania w społeczeństwie', '2025-06-29', 'szkatulka', 'Fundacja Dobre Państwo', true),

('Total Participation Management (TPM)', 'total-participation-management', 'Zarządzanie pełnią człowieczeństwa w nowoczesnych organizacjach', 'Total Participation Management to innowacyjna filozofia zarządzania, która stawia na pełne zaangażowanie wszystkich pracowników w proces decyzyjny organizacji. Model ten wykracza poza tradycyjne hierarchiczne struktury, tworząc przestrzeń dla kreatywności i innowacji. TPM zakłada, że każdy członek organizacji posiada unikalne kompetencje i perspektywę, które mogą przyczynić się do sukcesu całej organizacji. W artykule omawiamy praktyczne aspekty wdrażania TPM w polskich przedsiębiorstwach.', 'Zarządzanie pełnią człowieczeństwa w nowoczesnych organizacjach', '2025-06-29', 'szkatulka', 'Fundacja Dobre Państwo', true),

('Ekonomiczne niewolnictwo XXI wieku', 'ekonomiczne-niewolnictwo-xxi-wieku', 'Prekariat wobec outsourcingu - głos przeciwko wyzyskowi', 'XXI wiek przyniósł nowe formy eksploatacji ekonomicznej. Prekariat, klasa pracowników w niepewnych formach zatrudnienia, stał się ofiarą systemowego wyzysku korporacyjnego. Outsourcing, flexibilizacja pracy i pozorne korzyści gospodarki gig maskują rzeczywistość ekonomicznego niewolnictwa. Artykuł analizuje mechanizmy tego wyzysku i postuluje wprowadzenie gwarancji socjalnych dla wszystkich pracujących, niezależnie od formy zatrudnienia.', 'Prekariat wobec outsourcingu - głos przeciwko wyzyskowi', '2025-06-29', 'szkatulka', 'Fundacja Dobre Państwo', true),

('Wzmacnianie Społecznej Odpowiedzialności Biznesu', 'wzmacnianie-csr-biznesu', 'Strategiczne partnerstwa z interesariuszami', 'Społeczna odpowiedzialność biznesu przestała być opcjonalna - stała się koniecznością. W dobie wzrastającej świadomości społecznej i ekologicznej, przedsiębiorstwa muszą aktywnie angażować się w rozwiązywanie problemów społecznych. Artykuł przedstawia praktyczne podejście do budowania strategicznych partnerstw z organizacjami społecznymi, związkami zawodowymi i lokalnymi społecznościami. Omawiane są także aspekty raportowania ESG i budowania zaufania publicznego.', 'Strategiczne partnerstwa z interesariuszami', '2024-09-30', 'szczypta', 'Fundacja Dobre Państwo', true);